import React , {useState, useEffect} from 'react'; 
import axios from 'axios'; 
import '../css/test.css';
import loader from '../img/loading.gif'

function Test({prevSection, charId}) {

    const [isloading, setIsLoading] = useState(true);
    const [questions, setQuestions] = useState([]);
    const [char, setChar] = useState({});
    const [answers, setAnswers] = useState([]);

    useEffect(() => {

        const loadChar = async () => {
            const charObject = await axios('https://www.breakingbadapi.com/api/characters/' + charId);
            setChar(charObject.data);
            console.log(char);
            // let ans = [];
            // ans.push(char.status);
            // ans.push(char.nickname);
            // setAnswers(ans);
            // console.log(ans);

        }

        const loadQuestion = async () => {
            const que = await axios('http://localhost:5000/api/questions');
            console.log(que.data);
            setQuestions(que.data);
        }

        loadChar();
        loadQuestion();
        setIsLoading(false)

    }, [charId]);

    const goBack = (e) => {
        e.preventDefault();
        prevSection();
    }

    return ( isloading ? <div className="test-grid loader"><img src={loader} alt="loader" /></div> : <div className="test-grid row">

    </div>);
}

export default Test;
