import React, {useEffect} from 'react';
import axios from 'axios';
import './App.css';
import Header from './components/Header';
import CharacterGrid from './components/CharacterGrid';
import Pagination from './components/Pagination';
import SearchBox from './components/SearchBox';
import Test from './components/Test';
import store from './store/index';
import {createCharatersAction, createLoadingStateAction , createPageAction, createSearchValueAction, createSectionAction, createCharIdAction} from './actions/index'

const App = () => {

  // const [section, setSection] = useState(1);
  // const [characters, setcharacters] = useState([]);
  // const [loading, setloading] = useState(true);
  // const [page, setpage] = useState(1);
  // const [charPerPage] = useState(8);
  // const [searchValue, setsearchValue] = useState("");
  // const [charId, setCharId] = useState(1);  

  const lastChar = store.getState().page * store.getState().charPerPage;
  const firstChar = lastChar - store.getState().charPerPage;
  const currChars = store.getState().characters.slice(firstChar, lastChar);
  const totalPages = Math.ceil(store.getState().characters.length / store.getState().charPerPage);

  const pageChange = (pageNumber) => {
    store.dispatch(createPageAction(pageNumber));
  }

  const getInput = (inputValue) => {
    console.log(inputValue);
    store.dispatch(createSearchValueAction(inputValue));
  }

  const nextSection = () => {
    store.dispatch(createSectionAction(1));
  }

  const prevSection = () => {
    store.dispatch(createSectionAction(-1));
  }

  const getCharId = (id) => {
    store.dispatch(createCharIdAction(id));
  }

  useEffect(() => {
    const getChar = async () => {
      let chars = await axios('https://www.breakingbadapi.com/api/characters?name=' + store.getState().searchValue); // axios returns a promise
      store.dispatch(createCharatersAction(chars.data));
      store.dispatch(createLoadingStateAction(false));
    }

    getChar();
  }, [store.getState().searchValue]) // useEffect need a dependacy as parameter

  switch(store.getState().section)
  {
    case 1:
      return (
        <div className="Container">
        <Header />
        <SearchBox getInput={getInput}/>
        <CharacterGrid loading = {store.getState().loading} chars = {currChars} nextSection={nextSection} getCharId={getCharId}/>
        <Pagination totalPages={totalPages} pageChange={pageChange} />
        </div>
      );
    case 2:
      return (
        <div className="Container">
        <Header />
        <Test prevSection={prevSection} charId={store.getState().charId}/>
        </div>
      );

  }


}

export default App;
