import React, {useState} from 'react'


const Pagination = ({totalPages}) => {

    const pages = [];

    for( let i = 1 ; i <= totalPages ; i++)
    {
        pages.push(i);
    }

    return (

            <div className="page-grid" style={{ maxWidth: "768px", margin: "2rem auto", display: "flex", justifyContent:"center", alignItems: "center"}}>
                <ul class="pagination pagination-lg">
                    { pages.map((page, index) => (
                        <li key={page} class="page-item disabled" style={{background: "transparent"}}>
                            <a class="page-link" href="#" tabindex="-1">{page}</a>
                      </li>
                    ))}
                </ul>
            </div>
    )
}

export default Pagination;
