import React from 'react'
import "../css/pagination.css"


const Pagination = ({totalPages , setPage}) => {

    const pages = [];

    for( let i = 1 ; i <= totalPages ; i++)
    {
        pages.push(i);
    }

    const btnClicked = (page) => {
        console.log(page);
        setPage(page);
    }


    return (

            <div className="page-grid" style={{ maxWidth: "768px", margin: "2rem auto", display: "flex", justifyContent:"center", alignItems: "center"}}>
                <ul class="pagination pagination-lg">
                    { pages.map((page, index) => (
                        <li key={page} className="page-item disabled">
                            <button onClick={() => btnClicked(page)} className="page-link" href="!#" tabindex="-1">{page}</button>
                        </li>
                    ))}
                </ul>
            </div>
    )
}

export default Pagination;
