import React, {useState} from 'react'


const Pagination = ({totalPages}) => {

    const pages = [];

    for( let i = 1 ; i <= totalPages ; i++)
    {
        pages.push(i);
    }

    return (

            <div className="page-grid" style={{ margin: "2rem auto"}}>
                { pages.map((page, index) => (
                    <div key={page} style={{width: '40px', height: "40px", padding: "8px"}}> 
                        <p>{page}</p>
                    </div>
                ))}
            </div>
    )
}

export default Pagination;
