import React , {useState, useEffect} from 'react'; 
import axios from 'axios'; 
import '../css/test.css';
import loader from '../img/loading.gif'

function Test({prevSection}) {

    const [isloading, setIsLoading] = useState(true);
    const [questions, setQuestions] = useState([]);

    useEffect(() => {

        const load = async () => {
            const que = await axios('http://localhost:5000/api/questions');
            console.log(que.data);
            setQuestions(que.data);
        }

        load();
        setIsLoading(false)

    }, []);

    const goBack = (e) => {
        e.preventDefault();
        prevSection();
    }

    return ( isloading ? <div className="test-grid loader"><img src={loader} alt="loader" /></div> : <div className="test-grid row">

    </div>);
}

export default Test;
